             MyHandler h = new MyHandler();
             ManagementOperationObserver ob = new ManagementOperationObserver();
             ob.ObjectReady += new ObjectReadyEventHandler (h.NewObject);
             ob.Completed += new CompletedEventHandler (h.Done);
            
